$(document).ready(function(){
    var code=$(".codemirror-textarea")[0];
    var editor=CodeMirror.fromTextArea(code,{
        lineNumbers: true,
        lineWrapping: true,
        styleActiveLine: true,
        styleActiveSelected: true,
        mode: 'application/json',
        gutters: ["CodeMirror-lint-markers"],
        lint:true
        
    });
    $json="";
    $('#validate').click(function(){
        try {
        $json=editor.getValue();
        if(IsURL($json.toString())){
            $.getJSON($json.toString(),{},function(data,status){
                if(status=="success"){
                $dat=JSON.stringify(data);
                editor.setValue($dat);
                $djson=JSON.parse($dat);
                $('#error').show();
                $('#error').removeClass("alert-danger");
                $('#error').addClass("alert-success");
                $("#error").empty().append("Valid Json");
                }
                else{
                    alert("Noting Found on URL");
                }
                
                 
            });
        }else{
            $djson=$json.toString();
            $djson=jsonlint.parse($djson);
            $('#error').show();
            $('#error').removeClass("alert-danger");
            $('#error').addClass("alert-success");
            $("#error").empty().append("Valid Json"); 
        }       
        }
        catch(err) {
            var lineNumber=err;
            var regex = /[+-]?\d+(?:\.\d+)?/g;
            var str = lineNumber.toString();
            var lin;
            var match;
             while (match = regex.exec(str)) {
                 lin=match[0];
                    console.log(match[0]);
                    break;
                }
                $( ".CodeMirror-line:eq("+(lin-1)+")" ).css( "background-color", "#f8d7da");
                $('#error').show();
                $('#error').removeClass("alert-success");
                $('#error').addClass("alert-danger");
                 $("#error").empty().append(err);
            }
    })
    $('#reset').click(function(){
        editor.setValue('');
        $('#error').hide();
    })
    function IsURL(str) {
        var regex = /^(http:\/\/www\.|https:\/\/www\.|http:\/\/|https:\/\/)?[a-z0-9]+([\-\.]{1}[a-z0-9]+)*\.[a-z]{2,5}(:[0-9]{1,5})?(\/.*)?$/;
        if(regex .test(str)) {
            return true;
        } else {
          return false;
          
        }
      }


    
})
